import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminDashboard from './AdminDashboard';

const api = axios.create({ baseURL: 'http://localhost:5000/api' });

function App() {
    const [officer, setOfficer] = useState(localStorage.getItem('officer') || "");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('officer'));

  const [dayStarted, setDayStarted] = useState(false);
  const [village, setVillage] = useState("");
  const [photo, setPhoto] = useState(null);
  const [view, setView] = useState('officer');
  const [stats, setStats] = useState([]);
  // YAHAN ADD KAREIN
  const [userRole, setUserRole] = useState(localStorage.getItem('role') || 'officer'); 
  // ... baaki states


  const getLoc = () => new Promise((res, rej) => {
    navigator.geolocation.getCurrentPosition(p => res({ lat: p.coords.latitude, lng: p.coords.longitude }), () => rej("GPS Required"), { enableHighAccuracy: true });
  });

  const handleCapture = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => setPhoto(reader.result);
    reader.readAsDataURL(file);
  };

  const submitLog = async (type, payload) => {
    // START/END DAY ke liye photo zaroori nahi hai, baaki ke liye hai
    if (type !== 'Day Start' && type !== 'Day End') {
        if (!photo || !village) return alert("Photo and Village are mandatory!");
    }

    try {
        const loc = await getLoc();

        // URL ko backend ke hisaab se check karein (agar 404 aa raha hai)
        // Yahan maine '/activities' kar diya hai, jo aksar backend routes mein hota hai
        await api.post('/activities', { 
            type, 
            ...loc, 
            village: village || 'N/A', 
            officer, 
            payload, 
            photo,
            date: new Date().toLocaleDateString() // Date frontend se bhej rahe hain
        });

        alert(`${type} recorded successfully!`);
        setPhoto(null); 
        setVillage(""); 
        loadStats();
    } catch (err) { 
        console.error(err);
        alert("Error: Data save nahi hua. Backend URL check karein!"); 
    }
};
// Is function ko useEffect se upar kahin bhi rakh dein
const handleLogin = () => {
    const role = officer.toLowerCase() === 'admin' ? 'admin' : 'officer';
    localStorage.setItem('officer', officer);
    localStorage.setItem('role', role); // Role save ho gaya
    setUserRole(role);
    setIsLoggedIn(true);
};
  useEffect(() => { if (isLoggedIn) loadStats(); }, [isLoggedIn]);

  if (!isLoggedIn) return (
    <div style={styles.container}>
      <h2>üõ∞Ô∏è OCCAMY LOGIN</h2>
      <input style={styles.input} placeholder="Enter Name" onChange={e => setOfficer(e.target.value)} />
<button style={styles.btn} onClick={handleLogin}>LOGIN</button>    </div>
  );

  return (
    <div>
      <nav style={styles.nav}>
    <button onClick={() => setView('officer')}>üì± APP</button>

    {/* YAHAN CONDITION LAGAYEIN */}
    {userRole === 'admin' && (
        <button onClick={() => setView('admin')}>üìä DASHBOARD</button>
    )}
</nav>

<nav style={styles.nav}>
    <button onClick={() => setView('officer')}>üì± APP</button>
    {userRole === 'admin' && <button onClick={() => setView('admin')}>üìä DASHBOARD</button>}

    {/* Ye button add karein taaki storage clear ho sake */}
    <button 
        onClick={() => {
            localStorage.clear(); 
            window.location.reload();
        }} 
        style={{backgroundColor: '#e74c3c', color: 'white', marginLeft: '10px'}}
    >
        üö™ LOGOUT
    </button>
</nav>

      {view === 'officer' ? (
        <div style={styles.container}>
          <h3>Officer: {officer}</h3>
          {!dayStarted ? (
            <button style={styles.btn} onClick={() => { prompt("Start Odometer Reading:"); setDayStarted(true); }}>START DAY</button>
          ) : (
            <div style={styles.form}>
              <input type="file" accept="image/*" capture="environment" onChange={handleCapture} />
              <input style={styles.input} placeholder="Current Village" value={village} onChange={e => setVillage(e.target.value)} />

              <button style={styles.actionBtn} onClick={() => {
                const name = prompt("Farmer Name?");
                const cat = prompt("Category (Farmer/Seller/Influencer)?");
                const pot = prompt("Potential (kg)?");
                if(name && cat) submitLog('One-on-One', { name, cat, pot });
              }}>üë• ONE-ON-ONE MEETING</button>

              <button style={styles.actionBtn} onClick={() => {
                const attendees = prompt("Attendee Count?");
                if(attendees) submitLog('Group Meeting', { attendees });
              }}>üì¢ GROUP MEETING</button>

              <button style={styles.actionBtn} onClick={() => {
                const mode = prompt("Mode (B2B/B2C)?");
                const sku = prompt("Product SKU?");
                const qty = prompt("Quantity?");
                if(sku && qty) submitLog('Sale', { mode, sku, qty });
              }}>üõí LOG SALE</button>

              <button style={styles.actionBtn} onClick={() => {
                const qty = prompt("Sample Qty?");
                const purpose = prompt("Purpose (Trial/Demo)?");
                if(qty) submitLog('Sample', { qty, purpose });
              }}>üéÅ GIVE SAMPLE</button>

              <button style={styles.endBtn} onClick={() => { prompt("End Odometer Reading:"); setDayStarted(false); }}>END DAY</button>
            </div>
          )}
        </div>
      ) : <AdminDashboard stats={stats} />}
    </div>
  );
}

const styles = {
  container: { padding: '20px', maxWidth: '400px', margin: 'auto', textAlign: 'center', fontFamily: 'sans-serif' },
  input: { width: '100%', padding: '12px', marginBottom: '10px', borderRadius: '8px', border: '1px solid #ccc' },
  btn: { width: '100%', padding: '15px', background: '#27ae60', color: '#fff', border: 'none', borderRadius: '8px', fontWeight: 'bold' },
  actionBtn: { width: '100%', padding: '12px', background: '#3498db', color: '#fff', border: 'none', borderRadius: '8px', marginBottom: '8px' },
  endBtn: { width: '100%', padding: '12px', background: '#e74c3c', color: '#fff', border: 'none', borderRadius: '8px', marginTop: '15px' },
  nav: { background: '#2c3e50', padding: '10px', display: 'flex', justifyContent: 'space-around' },
  form: { display: 'flex', flexDirection: 'column', gap: '10px' }
};

export default App;
