import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminDashboard from './AdminDashboard'; 

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  withCredentials: true 
});

function App() {
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [dayStarted, setDayStarted] = useState(false);
  const [officerName, setOfficerName] = useState("");
  const [isOffline, setIsOffline] = useState(!navigator.onLine);
  const [view, setView] = useState('officer'); 
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [stats, setStats] = useState([]); // Shared Data for Dashboard

  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const getVerifiedLocation = () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        (err) => reject("Enable GPS!"),
        { enableHighAccuracy: true }
      );
    });
  };

  const handleLogin = async () => {
    if (!officerName) return alert("Please enter Officer Name");
    setLoading(true);
    try {
      await api.post('/login', { officerId: "OFF123", name: officerName });
      setIsLoggedIn(true);
      alert("Login Successful!");
    } catch (err) { alert("Login Failed"); }
    setLoading(false);
  };

  const logMeeting = async (type, additionalData = {}) => {
  if (!selectedPhoto) return alert("Bhai, photo click karna mandatory hai proof ke liye!"); // Verification check [cite: 28]

  setLoading(true);
  try {
    const loc = await getVerifiedLocation();

    // Photo ko turant dikhane ke liye URL banayein
    const photoURL = URL.createObjectURL(selectedPhoto);

    const newEntry = {
      id: Date.now(),
      date: new Date().toLocaleDateString('en-IN'),
      officer: officerName,
      village: additionalData.village || "Unknown",
      type: type,
      distance: (Math.random() * 1.5).toFixed(2), 
      photoPreview: photoURL, // Ye table mein photo dikhayega
      lat: loc.lat,
      lng: loc.lng
    };

    setStats(prev => [newEntry, ...prev]);
    // Backend ko bhejte waqt real file bhejni hogi (FormData use karke)
    alert("Activity Logged with Photo!");
    setSelectedPhoto(null); 
  } catch (err) { alert(err); }
  setLoading(false);
};

  const startDay = async () => {
    setLoading(true);
    try {
      const loc = await getVerifiedLocation();
      await api.post('/activity/start-day', loc);
      setDayStarted(true);
      alert("Day Started!");
    } catch (err) { alert("Error starting day"); }
    setLoading(false);
  };

  const endDay = async () => {
    setLoading(true);
    try {
      const res = await api.post('/activity/end-day');
      alert(`üèÅ DAY FINISHED! Total Meetings: ${stats.length}`);
      setDayStarted(false); 
    } catch (err) { alert("Error ending day"); }
    setLoading(false);
  };

  if (!isLoggedIn) {
    return (
      <div style={styles.app}>
        <h2 style={{textAlign: 'center'}}>OCCAMY LOGIN</h2>
        <input type="text" placeholder="Enter Officer Name" style={styles.input} onChange={(e) => setOfficerName(e.target.value)} />
        <button onClick={handleLogin} style={styles.bigGreenBtn} disabled={loading}>{loading ? "Logging..." : "LOGIN"}</button>
      </div>
    );
  }

  return (
    <div>
      <div style={{ background: '#2c3e50', padding: '10px', textAlign: 'center' }}>
        <button onClick={() => setView(view === 'officer' ? 'admin' : 'officer')} style={styles.switchBtn}>
          {view === 'officer' ? "üöÄ GO TO ADMIN DASHBOARD" : "üì± BACK TO OFFICER APP"}
        </button>
      </div>

      {view === 'officer' ? (
        <div style={styles.app}>
          {isOffline && <div style={styles.offlineAlert}>‚ö†Ô∏è OFFLINE MODE</div>}
          <h2 style={{ color: '#2c3e50' }}>Officer: {officerName}</h2>
          {!dayStarted ? (
            <button onClick={startDay} style={styles.bigGreenBtn}>START DAY</button>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              <div style={styles.photoBox}>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>üì∏ ATTACH PROOF:</label>
                <input type="file" accept="image/*" capture="environment" onChange={(e) => setSelectedPhoto(e.target.files[0])} />
                {selectedPhoto && <p style={{color: 'green', fontSize: '10px'}}>‚úÖ Photo Ready</p>}
              </div>
              <button onClick={() => logMeeting('Farmer Meeting')} style={styles.meetingBtn}>üë• FARMER MEETING</button>
              <button onClick={() => {
                 const sku = prompt("SKU Name?");
                 const qty = prompt("Quantity?");
                 if(sku && qty) logMeeting('Sales', { sku, quantity: qty, mode: 'Direct' });
              }} style={{...styles.meetingBtn, backgroundColor: '#3498db'}}>üõí LOG SALE</button>
              <button onClick={() => {
                const qty = prompt("Sample Quantity?");
                if(qty) logMeeting('Sample', { quantity: qty, purpose: 'Trial' });
              }} style={{...styles.meetingBtn, backgroundColor: '#9b59b6'}}>üéÅ GIVE SAMPLE</button>
              <button onClick={endDay} style={styles.endDayBtn}>END DAY</button>
            </div>
          )}
        </div>
      ) : (
        <AdminDashboard stats={stats} /> 
      )}
    </div>
  );
}

const styles = {
  app: { padding: '20px', maxWidth: '400px', margin: 'auto', fontFamily: 'Arial' },
  input: { width: '100%', padding: '12px', marginBottom: '10px', boxSizing: 'border-box' },
  bigGreenBtn: { width: '100%', padding: '15px', backgroundColor: '#27ae60', color: 'white', border: 'none', borderRadius: '10px', fontWeight: 'bold' },
  meetingBtn: { width: '100%', padding: '15px', backgroundColor: '#2ecc71', color: 'white', border: 'none', borderRadius: '8px', fontWeight: 'bold' },
  endDayBtn: { width: '100%', padding: '15px', marginTop: '20px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '8px', fontWeight: 'bold' },
  switchBtn: { padding: '10px 20px', backgroundColor: '#f39c12', border: 'none', borderRadius: '5px', color: 'white', fontWeight: 'bold' },
  photoBox: { border: '1px dashed #ccc', padding: '10px', borderRadius: '8px', backgroundColor: '#f9f9f9', marginBottom: '10px' },
  offlineAlert: { backgroundColor: '#ff9800', color: 'white', padding: '10px', borderRadius: '10px', marginBottom: '15px', textAlign: 'center' }
};

export default App;
