import React, { useState } from 'react';

const AdminDashboard = ({ stats }) => {
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth().toString());

    // Strict Filter Logic
    const filteredStats = stats.filter(s => {
        const dateToParse = s.timestamp || s.date;
        if (!dateToParse) return false;
        
        const entryDate = new Date(dateToParse);
        // Check if date is valid before getting month
        if (isNaN(entryDate.getTime())) return false;
        
        return entryDate.getMonth().toString() === selectedMonth;
    });

    const totalMeetings = filteredStats.length;
    const totalKM = filteredStats.reduce((acc, curr) => acc + parseFloat(curr.distance || 0), 0).toFixed(2);
    const activeOfficers = new Set(filteredStats.map(s => s.officer)).size;

    const b2cSales = filteredStats.filter(s => s.mode === 'B2C').length;
    const b2bSales = filteredStats.filter(s => s.mode === 'B2B').length;
    const totalSales = b2cSales + b2bSales;

    const generateReport = () => {
        const csvContent = "data:text/csv;charset=utf-8," 
            + "Date,Officer,Village,Type,Details,Distance\n"
            + filteredStats.map(e => {
                const detail = e.sku ? `${e.sku}(${e.qty})` : (e.attendees ? `${e.attendees} people` : "N/A");
                return `${e.date},${e.officer},${e.village},${e.type},${detail},${e.distance}`;
            }).join("\n");
        window.open(encodeURI(csvContent));
    };

    return (
        <div style={{ padding: '20px', backgroundColor: '#f0f2f5', minHeight: '100vh', fontFamily: 'Arial, sans-serif' }}>
            <h1 style={{ textAlign: 'center', color: '#1a73e8' }}>üõ∞Ô∏è Occamy Admin Dashboard</h1>
            
            <div style={{ display: 'flex', gap: '20px', marginBottom: '20px' }}>
                <div style={cardStyle}><h3>Officers Active</h3><p style={statNumberStyle}>{activeOfficers}</p></div>
                <div style={cardStyle}><h3>Total Activities</h3><p style={statNumberStyle}>{totalMeetings}</p></div>
                <div style={cardStyle}><h3>Total Travel</h3><p style={statNumberStyle}>{totalKM} KM</p></div>
            </div>

            <div style={{ ...cardStyle, marginBottom: '20px', textAlign: 'left' }}>
                <h4>üìä Sales (B2C: {b2cSales} | B2B: {b2bSales})</h4>
                {totalSales > 0 && (
                    <div style={{ display: 'flex', height: '20px', borderRadius: '10px', overflow: 'hidden', background: '#eee' }}>
                        <div style={{ width: `${(b2cSales/totalSales)*100}%`, background: '#3498db' }} />
                        <div style={{ width: `${(b2bSales/totalSales)*100}%`, background: '#2ecc71' }} />
                    </div>
                )}
            </div>

            <div style={{ display: 'flex', gap: '20px' }}>
                <div style={{ flex: 2, backgroundColor: 'white', padding: '20px', borderRadius: '12px' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
                        <h3 style={{margin:0}}>Activity Log</h3>
                        <div style={{display:'flex', gap:'10px'}}>
                            <select value={selectedMonth} onChange={(e) => setSelectedMonth(e.target.value)} style={{padding:'5px'}}>
                                {["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"].map((m, i) => (
                                    <option key={i} value={i}>{m}</option>
                                ))}
                            </select>
                            <button onClick={generateReport} style={{background:'#1a73e8', color:'white', border:'none', padding:'5px 10px', borderRadius:'4px', cursor:'pointer'}}>Export</button>
                        </div>
                    </div>

                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr style={{background:'#f8f9fa'}}>
                                <th style={thStyle}>Date</th>
                                <th style={thStyle}>Officer</th>
                                <th style={thStyle}>Type</th>
                                <th style={thStyle}>Details</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredStats.length === 0 ? (
                                <tr><td colSpan="4" style={{textAlign:'center', padding:'20px'}}>No records this month.</td></tr>
                            ) : (
                                filteredStats.map(s => (
                                    <tr key={s.id} style={{borderBottom:'1px solid #eee'}}>
                                        <td style={tdStyle}>{s.date}</td>
                                        <td style={tdStyle}>{s.officer}</td>
                                        <td style={tdStyle}>{s.type}</td>
                                        <td style={tdStyle}>{s.sku || s.attendees || "N/A"}</td>
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </div>

                <div style={{ flex: 1, backgroundColor: 'white', padding: '20px', borderRadius: '12px' }}>
                    <h3>üìç Location</h3>
                    {filteredStats.length > 0 ? (
                        <iframe 
                            title="map" width="100%" height="300" 
                            src={`https://maps.google.com/maps?q=${filteredStats[0].lat},${filteredStats[0].lng}&z=15&output=embed`}
                        ></iframe>
                    ) : <div style={{height:300, background:'#f8f9fa', display:'flex', alignItems:'center', justifyContent:'center'}}>No Data</div>}
                </div>
            </div>
        </div>
    );
};

const cardStyle = { flex: 1, padding: '20px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' };
const statNumberStyle = { fontSize: '24px', fontWeight: 'bold', color: '#1a73e8' };
const thStyle = { padding: '10px', textAlign: 'left', borderBottom: '2px solid #eee' };
const tdStyle = { padding: '10px' };

export default AdminDashboard;
