import React from 'react';

const AdminDashboard = ({ stats }) => {
  const b2b = stats.filter(s => s.payload?.mode === 'B2B').length;
  const b2c = stats.filter(s => s.payload?.mode === 'B2C').length;
  const meetings = stats.filter(s => s.type.includes('Meeting')).length;

  return (
    <div style={{ padding: '20px', background: '#f4f7f6', minHeight: '100vh' }}>
      <h2 style={{ color: '#2c3e50' }}>üõ∞Ô∏è Occamy Admin Control</h2>
      
      <div style={grid}>
        <div style={card}><h3>Activities</h3><p>{stats.length}</p></div>
        <div style={card}><h3>Meetings</h3><p>{meetings}</p></div>
        <div style={card}><h3>B2B Sales</h3><p>{b2b}</p></div>
        <div style={card}><h3>B2C Sales</h3><p>{b2c}</p></div>
      </div>

      <div style={tableContainer}>
        <h3>Verified Activity Logs</h3>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid #ddd', textAlign: 'left' }}>
              <th>Officer</th>
              <th>Village</th>
              <th>Type</th>
              <th>Details</th>
              <th>Proof</th>
            </tr>
          </thead>
          <tbody>
            {stats.map(s => (
              <tr key={s.id} style={{ borderBottom: '1px solid #eee' }}>
                <td style={{padding: '10px'}}>{s.officer}</td>
                <td>{s.village}</td>
                <td>{s.type}</td>
                <td>{JSON.stringify(s.payload)}</td>
<td>{s.photo && <a href={`http://localhost:5000${s.photo}`} target="_blank" rel="noreferrer">üì∏ View</a>}</td>              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const grid = { display: 'flex', gap: '15px', marginBottom: '20px' };
const card = { flex: 1, background: '#fff', padding: '15px', borderRadius: '10px', textAlign: 'center', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' };
const tableContainer = { background: '#fff', padding: '20px', borderRadius: '10px' };

export default AdminDashboard;
