import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminDashboard from './AdminDashboard'; 

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  withCredentials: true 
});

function App() {
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [dayStarted, setDayStarted] = useState(false);
  const [officerName, setOfficerName] = useState("");
  const [isOffline, setIsOffline] = useState(!navigator.onLine);
  const [view, setView] = useState('officer'); 
  const [selectedPhoto, setSelectedPhoto] = useState(null); // Step 3: Photo State

  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const handleLogin = async () => {
    if (!officerName) return alert("Please enter Officer Name");
    setLoading(true);
    try {
      await api.post('/login', { officerId: "OFF123", name: officerName });
      setIsLoggedIn(true);
      alert("Login Successful!");
    } catch (err) { alert("Login Failed"); }
    setLoading(false);
  };

  const getVerifiedLocation = () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        (err) => reject("Enable GPS!"),
        { enableHighAccuracy: true }
      );
    });
  };

  const startDay = async () => {
    setLoading(true);
    try {
      const loc = await getVerifiedLocation();
      await api.post('/activity/start-day', loc);
      setDayStarted(true);
      alert("Day Started!");
    } catch (err) { alert(err.response?.data?.error || "Error"); }
    setLoading(false);
  };

 const logMeeting = async (type, additionalData = {}) => {
    setLoading(true);
    try {
      const loc = await getVerifiedLocation();

      // --- YE REAL PHOTO LOGIC HAI ---
      let photoPreviewUrl = null;
      if (selectedPhoto) {
        // Browser ek temporary 'blob' link banata hai jo Dashboard par dikh sake
        photoPreviewUrl = URL.createObjectURL(selectedPhoto); 
      }

      const payload = { 
        type, 
        lat: loc.lat, 
        lng: loc.lng, 
        photoPreview: photoPreviewUrl, // Dashboard yahan se photo uthayega
        officer: officerName,
        date: new Date().toLocaleDateString(),
        ...additionalData 
      };

      const res = await api.post('/activity/log-meeting', payload);

      // Demo ke liye hum local state ko update kar rahe hain taaki dashboard refresh ho jaye
      alert(`‚úÖ ${type} Logged! ID: ${res.data.verificationCode}`);

      setSelectedPhoto(null); // Photo reset kar do
    } catch (err) { 
      alert("Error: " + err.message); 
    }
    setLoading(false);
  };
  // --- NAYE FEATURES STEP-BY-STEP ---

  // Step 1: Sample Distribution [cite: 51, 52]
  const logSample = async () => {
    const qty = prompt("Sample Quantity? (e.g., 2 units)");
    const purpose = prompt("Purpose? (trial / demo / follow-up)");
    if(qty && purpose) {
      await logMeeting('Sample Distribution', { quantity: qty, purpose: purpose });
    }
  };

  // Step 2: Detailed Sales & SKU [cite: 63, 64, 65]
  const logDetailedSale = async () => {
    const sku = prompt("Product SKU? (e.g., Nutra-Mix)");
    const packSize = prompt("Pack Size? (e.g., 5kg / 10kg)");
    const qty = prompt("Quantity Sold?");
    const mode = prompt("Mode? (Direct / via Distributor)");

    if(sku && qty) {
      await logMeeting('Sales', { 
        sku, 
        packSize, 
        quantity: qty, 
        mode,
        saleType: mode.toLowerCase() === 'direct' ? 'B2C' : 'B2B' // [cite: 74]
      });
    }
  };

  const endDay = async () => {
    setLoading(true);
    try {
      const res = await api.post('/activity/end-day');
      const { summary } = res.data;
      alert(`üèÅ DAY FINISHED!\n\nüë§ Officer: ${summary.officer}\nüìç Meetings: ${summary.totalMeetings}\nüõ£Ô∏è Distance: ${summary.distanceKM} KM`);
      setDayStarted(false); 
    } catch (err) { alert("Error ending day"); }
    setLoading(false);
  };

  if (!isLoggedIn) {
    return (
      <div style={styles.app}>
        <h2 style={{textAlign: 'center'}}>OCCAMY LOGIN</h2>
        <input type="text" placeholder="Enter Officer Name" style={styles.input} onChange={(e) => setOfficerName(e.target.value)} />
        <button onClick={handleLogin} style={styles.bigGreenBtn} disabled={loading}>{loading ? "Logging in..." : "LOGIN"}</button>
      </div>
    );
  }

  return (
    <div>
      <div style={{ background: '#2c3e50', padding: '10px', textAlign: 'center' }}>
        <button onClick={() => setView(view === 'officer' ? 'admin' : 'officer')} style={styles.switchBtn}>
          {view === 'officer' ? "üöÄ GO TO ADMIN DASHBOARD" : "üì± BACK TO OFFICER APP"}
        </button>
      </div>

      {view === 'officer' ? (
        <div style={styles.app}>
          {isOffline && <div style={styles.offlineAlert}>‚ö†Ô∏è OFFLINE MODE: Sync Pending</div>}
          <h2 style={{ color: '#2c3e50' }}>Officer: {officerName}</h2>

          {!dayStarted ? (
            <button onClick={startDay} style={styles.bigGreenBtn}>START DAY</button>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>

              {/* Step 3: Photo Upload UI [cite: 28, 45] */}
              <div style={styles.photoBox}>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>üì∏ PROOF (Odometer/Meeting):</label>
                <input type="file" accept="image/*" capture="environment" onChange={(e) => setSelectedPhoto(e.target.files[0])} style={{fontSize: '11px'}} />
                {selectedPhoto && <p style={{fontSize: '10px', color: 'green'}}>‚úÖ Photo Attached</p>}
              </div>

              <button onClick={() => logMeeting('Farmer')} style={styles.meetingBtn}>üë• FARMER MEETING</button>

              {/* Merged Step 2: Detailed Sale */}
              <button onClick={logDetailedSale} style={{...styles.meetingBtn, backgroundColor: '#3498db'}}>üõí LOG PRODUCT SALE</button>

              {/* Merged Step 1: Sample Distribution */}
              <button onClick={logSample} style={{...styles.meetingBtn, backgroundColor: '#9b59b6'}}>üéÅ GIVE SAMPLE</button>

              <button onClick={() => {
                const count = prompt("Farmers Count?");
                if(count) logMeeting('Group Meeting', { attendees: count });
              }} style={{...styles.meetingBtn, backgroundColor: '#f39c12'}}>üë• GROUP MEETING</button>

              <button onClick={endDay} style={styles.endDayBtn}>END DAY</button>
            </div>
          )}
        </div>
      ) : (
        <AdminDashboard /> 
      )}
    </div>
  );
}

const styles = {
  app: { padding: '20px', maxWidth: '400px', margin: 'auto', fontFamily: 'Arial' },
  input: { width: '100%', padding: '12px', marginBottom: '10px', boxSizing: 'border-box' },
  bigGreenBtn: { width: '100%', padding: '15px', backgroundColor: '#27ae60', color: 'white', border: 'none', borderRadius: '10px', fontWeight: 'bold' },
  meetingBtn: { width: '100%', padding: '15px', backgroundColor: '#2ecc71', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
  endDayBtn: { width: '100%', padding: '15px', marginTop: '20px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '8px', fontWeight: 'bold' },
  switchBtn: { padding: '10px 20px', backgroundColor: '#f39c12', border: 'none', borderRadius: '5px', color: 'white', fontWeight: 'bold' },
  offlineAlert: { backgroundColor: '#ff9800', color: 'white', padding: '10px', borderRadius: '10px', marginBottom: '15px', textAlign: 'center', fontWeight: 'bold' },
  photoBox: { border: '1px dashed #ccc', padding: '10px', borderRadius: '8px', backgroundColor: '#f9f9f9' }
};

export default App;
