import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AdminDashboard from './AdminDashboard';

const api = axios.create({ baseURL: 'http://localhost:5000/api' });

function App() {
  const [officerName, setOfficerName] = useState(localStorage.getItem('officer') || "");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('officer'));
  const [dayStarted, setDayStarted] = useState(false);
  const [isOffline, setIsOffline] = useState(!navigator.onLine);
  const [stats, setStats] = useState([]);
  const [villageName, setVillageName] = useState("");
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [view, setView] = useState('officer');

  useEffect(() => {
    const handleStatus = () => setIsOffline(!navigator.onLine);
    window.addEventListener('online', handleStatus);
    window.addEventListener('offline', handleStatus);
    loadStats();
    return () => { window.removeEventListener('online', handleStatus); window.removeEventListener('offline', handleStatus); };
  }, []);

  const loadStats = async () => {
    try {
      const res = await api.get('/admin/stats');
      setStats(res.data);
    } catch (e) { console.log("Offline mode: using local data"); }
  };

  const getLoc = () => new Promise((res, rej) => {
    navigator.geolocation.getCurrentPosition(p => res({ lat: p.coords.latitude, lng: p.coords.longitude }), () => rej("GPS Required"), { enableHighAccuracy: true });
  });

  const logActivity = async (type, payload) => {
    if (!selectedPhoto || !villageName) return alert("Photo and Village are mandatory!");
    const loc = await getLoc();
    const activityData = { officer: officerName, type, lat: loc.lat, lng: loc.lng, village: villageName, payload };

    if (isOffline) {
      const pending = JSON.parse(localStorage.getItem('pending_logs') || "[]");
      localStorage.setItem('pending_logs', JSON.stringify([...pending, activityData]));
      alert("Saved Offline! Sync when online.");
    } else {
      await api.post('/activity/log-activity', activityData);
      alert("Logged Successfully!");
      loadStats();
    }
    setSelectedPhoto(null); setVillageName("");
  };

  const syncData = async () => {
    const pending = JSON.parse(localStorage.getItem('pending_logs') || "[]");
    if (pending.length === 0) return alert("Nothing to sync");
    for (let log of pending) { await api.post('/activity/log-activity', log); }
    localStorage.removeItem('pending_logs');
    alert("All data synced to server!");
    loadStats();
  };

  if (!isLoggedIn) return (
    <div style={styles.app}>
      <h2>üõ∞Ô∏è OCCAMY LOGIN</h2>
      <input style={styles.input} placeholder="Officer Name" onChange={e => setOfficerName(e.target.value)} />
      <button style={styles.bigGreenBtn} onClick={() => { localStorage.setItem('officer', officerName); setIsLoggedIn(true); }}>LOGIN</button>
    </div>
  );

  return (
    <div>
      <div style={{ background: '#2c3e50', padding: '10px', display: 'flex', justifyContent: 'space-around' }}>
        <button onClick={() => setView(view === 'officer' ? 'admin' : 'officer')} style={styles.switchBtn}>
          {view === 'officer' ? "DASHBOARD" : "OFFICER APP"}
        </button>
        {isOffline ? <span style={{color: 'orange'}}>üî¥ Offline</span> : <button onClick={syncData} style={styles.syncBtn}>üîÑ Sync Now</button>}
      </div>

      {view === 'officer' ? (
        <div style={styles.app}>
          <h3>Officer: {officerName}</h3>
          {!dayStarted ? (
            <button style={styles.bigGreenBtn} onClick={() => { prompt("Enter Start Odometer Reading:"); setDayStarted(true); }}>START DAY</button>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              <input type="file" capture="environment" onChange={e => setSelectedPhoto(e.target.files[0])} />
              <input style={styles.input} placeholder="Village Name" value={villageName} onChange={e => setVillageName(e.target.value)} />

              <button style={styles.meetingBtn} onClick={() => logActivity('Sale', { mode: prompt("B2B or B2C?"), qty: prompt("Quantity") })}>üõí LOG SALE</button>
              <button style={{...styles.meetingBtn, background: '#9b59b6'}} onClick={() => logActivity('Meeting', { type: 'Farmer' })}>üë• MEETING</button>

              <button style={styles.endDayBtn} onClick={() => { prompt("Enter End Odometer Reading:"); setDayStarted(false); }}>END DAY</button>
            </div>
          )}
        </div>
      ) : <AdminDashboard stats={stats} />}
    </div>
  );
}

const styles = {
  app: { padding: '20px', maxWidth: '400px', margin: 'auto', textAlign: 'center' },
  input: { width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '5px', border: '1px solid #ddd' },
  bigGreenBtn: { width: '100%', padding: '15px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' },
  meetingBtn: { width: '100%', padding: '12px', background: '#3498db', color: 'white', border: 'none', borderRadius: '8px' },
  endDayBtn: { width: '100%', padding: '12px', background: '#e74c3c', color: 'white', border: 'none', borderRadius: '8px', marginTop: '20px' },
  switchBtn: { background: '#f39c12', color: 'white', border: 'none', padding: '8px 15px', borderRadius: '5px' },
  syncBtn: { background: '#2ecc71', color: 'white', border: 'none', padding: '8px 15px', borderRadius: '5px' }
};

export default App;
