import React, { useState } from 'react';
import axios from 'axios';
import AdminDashboard from './AdminDashboard'; // Isse ensure kar ki AdminDashboard.js same folder mein hai

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  withCredentials: true 
});

function App() {
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [dayStarted, setDayStarted] = useState(false);
  const [officerName, setOfficerName] = useState("");

  const [isOffline, setIsOffline] = useState(!navigator.onLine);

  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // --- YE HAI TERA NAYA SWITCH STATE ---
  const [view, setView] = useState('officer'); 

  // --- TERA PURANA LOGIN/GPS LOGIC ---
  const handleLogin = async () => {
    if (!officerName) return alert("Please enter Officer Name");
    setLoading(true);
    try {
      await api.post('/login', { officerId: "OFF123", name: officerName });
      setIsLoggedIn(true);
      alert("Login Successful!");
    } catch (err) { alert("Login Failed"); }
    setLoading(false);
  };

  const getVerifiedLocation = () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        (err) => reject("Enable GPS!"),
        { enableHighAccuracy: true }
      );
    });
  };

  const startDay = async () => {
    setLoading(true);
    try {
      const loc = await getVerifiedLocation();
      await api.post('/activity/start-day', loc);
      setDayStarted(true);
      alert("Day Started!");
    } catch (err) { alert(err.response?.data?.error || "Error"); }
    setLoading(false);
  };

  // App.js mein logMeeting ko aise update karo
const logMeeting = async (type, additionalData = {}) => {
  setLoading(true);
  try {
    const loc = await getVerifiedLocation();
    const payload = {
      type,
      lat: loc.lat,
      lng: loc.lng,
      ...additionalData // Yahan Sales/Sample ka data jayega
    };
    const res = await api.post('/activity/log-meeting', payload);
    alert(`‚úÖ ${type} Logged! ID: ${res.data.verificationCode}`);
  } catch (err) {
    alert("Error: " + err.message);
  }
  setLoading(false);
};



  const endDay = async () => {
    setLoading(true);
    try {
      const res = await api.post('/activity/end-day');
      const { summary } = res.data;
      alert(`üèÅ DAY FINISHED!\n\nüë§ Officer: ${summary.officer}\nüìç Meetings: ${summary.totalMeetings}\nüõ£Ô∏è Distance: ${summary.distanceKM} KM`);
      setDayStarted(false);
    } catch (err) { alert("Error ending day"); }
    setLoading(false);
  };

  // --- UI SCREENS LOGIC ---

  // 1. Agar login nahi hai toh login dikhao
  if (!isLoggedIn) {
    return (
      <div style={styles.app}>
        <h2>OCCAMY LOGIN</h2>
        <input type="text" placeholder="Enter Officer Name" style={styles.input} onChange={(e) => setOfficerName(e.target.value)} />
// UI mein buttons ko aise change karo:
<button onClick={() => logMeeting('Sales', { sku: 'Nutra-Mix', qty: 5, price: 500 })} style={styles.meetingBtn}>
  üõí LOG SALE (5kg Nutra)
</button>
<button onClick={() => logMeeting('Sample', { sampleQty: 2, purpose: 'Trial' })} style={styles.meetingBtn}>
  üéÅ GIVE SAMPLE (2 units)
</button>   
// App.js ke UI mein ek naya button:
<button onClick={() => {
  const count = prompt("Enter number of farmers:");
  if(count) logMeeting('Group Meeting', { attendees: count, village: 'Kashipur' });
}} style={styles.meetingBtn}>
  üë• FARMER GROUP MEETING
</button>  
 </div>
    );
  }

  // 2. Agar login hai, toh check karo view kaunsa hai
  // ... baaki functions jaise startDay, logMeeting upar rahenge ...

  return (
    <div>
      {/* 1. Header Switch (Admin aur Officer ke beech switch karne ke liye) */}
      <div style={{ background: '#2c3e50', padding: '10px', textAlign: 'center' }}>
        <button 
          onClick={() => setView(view === 'officer' ? 'admin' : 'officer')} 
          style={{ padding: '10px 20px', backgroundColor: '#f39c12', border: 'none', borderRadius: '5px', color: 'white', fontWeight: 'bold', cursor: 'pointer' }}
        >
          {view === 'officer' ? "üöÄ GO TO ADMIN DASHBOARD" : "üì± BACK TO OFFICER APP"}
        </button>
      </div>

      {view === 'officer' ? (
        <div style={styles.app}>

          {/* ‚úÖ OFFLINE ALERT (Sabse upar dikhega) */}
          {isOffline && (
            <div style={{ 
              backgroundColor: '#ff9800', 
              color: 'white', 
              padding: '10px', 
              borderRadius: '10px', 
              marginBottom: '15px', 
              fontWeight: 'bold',
              textAlign: 'center'
            }}>
              ‚ö†Ô∏è OFFLINE MODE: Data will sync automatically 
            </div>
          )}

          <h2 style={{ color: '#2c3e50' }}>Officer: {officerName || "Not Logged In"}</h2>

          {!dayStarted ? (
            <button onClick={startDay} style={styles.bigGreenBtn}>START DAY</button>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              {/* Mandatory Meeting & Sales Tracking Buttons */}
              <button onClick={() => logMeeting('Farmer')} style={styles.meetingBtn}>üë• LOG FARMER MEETING</button>

              {/* Page 3 ki mandatory requirements ke buttons yahan dalo */}
              <button onClick={() => logMeeting('Sales', { sku: 'Nutra-Mix', qty: 1 })} style={{...styles.meetingBtn, backgroundColor: '#3498db'}}>üõí LOG PRODUCT SALE</button>
              <button onClick={() => logMeeting('Sample', { qty: 1 })} style={{...styles.meetingBtn, backgroundColor: '#9b59b6'}}>üéÅ GIVE SAMPLE</button>

              <button onClick={endDay} style={styles.endDayBtn}>END DAY</button>
            </div>
          )}
        </div>
      ) : (
        <AdminDashboard /> 
      )}
    </div>
  );
} // App function ka close

// Styles objects yahan rahenge
const styles = {
  app: { padding: '20px', maxWidth: '400px', margin: 'auto', fontFamily: 'Arial' },
  bigGreenBtn: { width: '100%', padding: '20px', fontSize: '20px', backgroundColor: '#27ae60', color: 'white', border: 'none', borderRadius: '10px' },
  meetingBtn: { width: '100%', padding: '15px', fontSize: '16px', backgroundColor: '#2ecc71', color: 'white', border: 'none', borderRadius: '8px' },
  endDayBtn: { width: '100%', padding: '15px', marginTop: '20px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '8px' }
};

export default App;
